  let [inputValue, setValue] = useState(1);
  let formRef = useRef();
  let handlerForm = (event) => {
    event.preventDefault();
    let enteredAmount = +formRef.current.value;
    props.onAddToCart(enteredAmount);
  };
  return (
    <form className={classes.form} onSubmit={handlerForm}>
      <Input
        value={i}
        label="Amount"
        id={props.id}
        type="number"
        min="1"
        
      />
      <button>+Add</button>
    </form>
  );
};
