  let [showModal, setModal] = useState(false);

  let showingModal = () => {
    setModal(true);
  };
  let hidingModal = () => {
    setModal(false);
  };
  return (
    <Context.Provider
      value={{
        items: [],
        totalAmount: 0,
        addItem: addItemToCart,
        removeItem: removeItemFromCart,
      }}
    >
      <Header onSetModal={showingModal} />
      <Meals />
      {showModal && <Modal onSetModal={hidingModal} />}
    </Context.Provider>
  );
};
export default App;
